mapper.insert:
/* Expect - expectInsert */
select expect_seq.nextval from dual
insert into expect(s_num, ex_no, ex_title, u_id, ex_content) values(#{s_num}, #{ex_no}, #{ex_title}, #{u_id}, #{ex_content})

mapper.update:
/* expect - expectUpdate */
UPDATE expect
SET ex_content = #{ex_content}
	, ex_regdate = SYSDATE
WHERE ex_no = #{ex_no}
