mapper.insert:

select expect_seq.nextval from dual

insert into expect(ex_no, s_num, ex_title, u_id, ex_content
)values(
	#{ex_no}, #{s_num}, #{ex_title}, #{u_id}, #{ex_content}
	)

mapper.update:
/* expect - expectUpdate */
UPDATE expect
SET ex_content = #{ex_content}
	, ex_title = #{ex_title}
	, ex_regdate = SYSDATE
WHERE ex_no = #{ex_no}
