mapper.select:
SELECT rnum, ti_num, ti_regdate, pay_num, hall_date, s_num, s_name, COUNT(seat_num) AS seat_num, u_id, ti_status, hall_id FROM ( SELECT rnum, t.ti_num, to_char(t.ti_regdate,'yyyy-mm-dd') AS ti_regdate, t.pay_num, h.hall_id, hall_date, s.s_num, s_name, se.seat_num, t.u_id, t.ti_status FROM ( SELECT rownum AS rnum, ti_num, ti_regdate, u_id, ti_status, pay_num FROM ticket WHERE u_id = 'user02' ORDER BY ti_regdate desc ) t INNER JOIN ticket_seat ts ON t.ti_num = ts.ti_num INNER JOIN seat se ON ts.seat_num = se.seat_num INNER JOIN hall h ON se.hall_id = h.hall_id INNER JOIN theater th ON h.th_num = th.th_num INNER JOIN show s ON th.th_num = s.th_num INNER JOIN pay p ON t.pay_num = p.pay_num WHERE
hall_date between #{start_date} and #{end_date}
to_char(ti_regdate, 'YYYY-MM-DD') between #{start_date} and #{end_date}
rownum <= #{pageNum} * #{amount} ) WHERE rnum > (#{pageNum} -1) * #{amount} GROUP BY rnum, ti_num, ti_regdate, pay_num, hall_date, s_num, s_name, u_id, ti_status, hall_id

mapper.update:
UPDATE pay
SET pay_status = 0
WHERE pay_status = 1 AND pay_num = #{pay_num}

mapper.select:
select * from (
    select * from faq 
    where f_category = #{f_category}
    order by DBMS_RANDOM.RANDOM
)
 where rownum <= 5

SELECT COUNT(*) likesCount
FROM bookmark
