mapper.select:
SELECT rnum, ti_num, ti_regdate, seat_date, s_num, s_name, hall_turn, hall_time, COUNT(seat_num) AS seat_num, u_id, ti_status FROM ( SELECT rnum, t.ti_num, to_char(t.ti_regdate,'yyyy-mm-dd') AS ti_regdate, to_char(t.seat_date,'yyyy-mm-dd') AS seat_date, s_num, s_name, h.hall_turn, h.hall_time, se.seat_num, t.u_id, t.ti_status FROM ( SELECT rownum AS rnum, ti_num, ti_regdate, seat_date, u_id, ti_status FROM ticket ORDER BY seat_date desc ) t JOIN ticket_seat ts ON t.ti_num = ts.ti_num JOIN seat se ON ts.seat_num = se.seat_num JOIN hall h ON se.hall_id = h.hall_id JOIN theater th ON h.th_num = th.th_num JOIN show s ON th.th_num = s.th_num WHERE
to_char(seat_date, 'YYYY-MM-DD') between #{start_date} and #{end_date}
to_char(ti_regdate, 'YYYY-MM-DD') between #{start_date} and #{end_date}
rownum <= #{pageNum} * #{amount} AND u_id = #{u_id} ) WHERE rnum > (#{pageNum} -1) * #{amount} GROUP BY rnum, ti_num, ti_regdate, seat_date, s_num, s_name, hall_turn, hall_time, u_id, ti_status

mapper.update:
UPDATE ticket
SET ti_status = 0
WHERE ti_num = #{ti_num}

mapper.select:
SELECT COUNT(*) likesCount
FROM bookmark
WHERE s_num = ${s_num}

SELECT COUNT(*)
FROM bookmark
WHERE u_id = #{u_id}

SELECT COUNT(*)
FROM qna
WHERE u_id = #{u_id}
