mapper.select:
/* Qna - qna Detail */
SELECT
	q_no, u_id, q_title, q_content, s_num,
	TO_CHAR(q_regdate, 'YYYY-MM-DD') AS q_regdate
FROM qna
WHERE q_no = #{q_no} and s_num = #{s_num}

mapper.update:
		/* Qna - qnaUpdate */
		UPDATE qna
		SET q_content = #{q_content},
			q_title = #{q_title},
        	q_update = SYSDATE
		WHERE q_no = #{q_no} and s_num = #{s_num}
