mapper.insert:
/* Reply - replyInsert */
select qna_reply_seq.nextval from dual
insert into qna_reply(q_no, q_r_no, u_id, q_r_content) values(#{q_no}, #{q_r_no}, #{u_id}, #{q_r_content})

mapper.update:
/* qnaReply - qnaReplyUpdate */
UPDATE qna_reply
SET q_r_content = #{q_r_content}
	, q_r_update = SYSDATE
WHERE q_r_no = #{q_r_no}
