mapper.select:
select s_num, s_name, th_num, th_name, s_genre, s_opendate, s_closedate, s_price, s_point
from(select 


rownum as rnum, s_num, s_name, th_num, s_genre, to_char(s_opendate,'yyyy-MM-dd') s_opendate, to_char(s_closedate,'yyyy-MM-dd') s_closedate, s_price, s_point,
(select theater.th_name from theater where theater.th_num = show.th_num) as th_name 
from show
where 




rownum <= #{pageNum}*#{amount}
) showlist
where rnum>(#{pageNum}-1)*#{amount}


select s_num, s_name, th_num, th_name, s_genre, s_price, s_point, rank_rank, rank_ticket,
to_char(s_opendate,'yyyy-MM-dd') s_opendate, to_char(s_closedate,'yyyy-MM-dd') s_closedate,
to_char(rank_start,'yyyy-MM-dd') rank_start, to_char(rank_end,'yyyy-MM-dd') rank_end 


	, s_point_rank


	, decode(rank_prev, 0, 'new', to_char(rank_prev - rank_rank)) rank_change

from(select rownum as rnum, s.s_num as s_num, s_name, th_num, s_genre, s_opendate, s_closedate, s_price, s_point, 
rank_start, rank_end as rank_end, rank_rank, rank_ticket,

	(dense_rank() over (order by s_point desc)) s_point_rank,




(select theater.th_name from theater where theater.th_num = s.th_num) as th_name 
from show s, rank r 
where s.s_num = r.s_num 
and r.rank_start = #{start_date}
and r.rank_end = #{end_date}
and rownum <= #{pageNum}*#{amount}
and s_genre = #{s_genre}
order by rank_rank
order by s_point_rank
) ranklist
where rnum>(#{pageNum}-1)*#{amount}
