mapper.select:
select hallList.s_num, s_name ,th_name, TO_CHAR(to_date(hall_end,'mm/dd/yy'),'yyyy-mm-dd') as hall_end,TO_CHAR(to_date(hall_start,'mm/dd/yy'),'yyyy-mm-dd') as hall_start, TO_DATE(to_date(hall_end,'mm/dd/yy'), 'YYYY-MM-DD') - TO_DATE(to_date(hall_start,'mm/dd/yy'), 'YYYY-MM-DD') + 1 as date_gap, count(h.hall_id) as data_num from ( select ROWNUM as rnum ,th_num,s_num,s_name from show WHERE
rownum <= #{pageNum} * #{amount} order by s_num ) hallList LEFT JOIN hall h ON h.s_num = hallList.s_num and h.th_num = hallList.th_num LEFT JOIN theater t ON h.th_num = t.th_num WHERE rnum > (#{pageNum}-1) * #{amount}
group by hallList.s_num,s_name ,th_name, hall_end, hall_start order by hallList.s_num

mapper.update:
UPDATE seat 
SET seat_name = #{seat_name},
	seat_status = #{seat_status},
	seat_age = #{seat_age},
	seat_update = #{seat_update}
where seat_num=#{seat_num} and hall_id = #{hall_id}

UPDATE pay 
SET pay_status = 0
WHERE pay_num = #{pay_num}

UPDATE ticket 
SET ti_status = 0
WHERE pay_num = #{pay_num}
