mapper.insert:
/* review - reviewInsert */


    select reviewboard_seq.nextval from dual        

	insert into reviewboard(
		r_no, u_id, r_title, r_content, r_point, r_hit, s_num
	)values(
		#{r_no}, #{u_id}, #{r_title}, #{r_content}, #{r_point}, #{r_hit}, #{s_num}
	)

mapper.update:
 	/* review - reviewUpdate */
 	update reviewboard 
 	set r_content = #{r_content}
		,r_update = sysdate		 	
	where r_no = #{r_no}
