mapper.select:
select th_num, th_name, th_seat, th_addr, th_map_lat, th_map_lng, th_web, 
to_char(th_regdate,'yyyy-MM-dd') th_regdate, to_char(th_update,'yyyy-MM-dd') th_update
from(
select 


rownum as rnum, th_num, th_name, th_seat, th_addr, th_map_lat, th_map_lng, th_web, th_regdate, th_update
from theater
where




rownum <= #{pageNum}*#{amount}
) theaterlist
where rnum>(#{pageNum}-1)*#{amount}

select th_num, th_name, th_seat, th_addr, th_map_lat, th_map_lng, th_web 
from theater
where th_num=#{th_num}
