mapper.select:
select s_num, s_name, th_num, th_name, s_genre, s_opendate, s_closedate, s_point 
from(
select 


rownum as rnum, s_num, s_name, th_num, s_genre, to_char(s_opendate,'yyyy-MM-dd') s_opendate, to_char(s_closedate,'yyyy-MM-dd') s_closedate, s_point, 
(select theater.th_name from theater where theater.th_num = show.th_num) as th_name 
from show
where 




rownum <= #{pageNum}*#{amount}
) showlist
where rnum>(#{pageNum}-1)*#{amount}


select s_num, s_name, th_num, th_name, s_genre, to_char(s_opendate,'yyyy-MM-dd') s_opendate, to_char(s_closedate,'yyyy-MM-dd') s_closedate, s_point, rank_rank, rank_ticket, th_name 
from(select rownum as rnum, s.s_num as s_num, s_name, th_num, s_genre, s_opendate, s_closedate, s_point, rank_rank, rank_ticket,
(select theater.th_name from theater where theater.th_num = s.th_num) as th_name 
from show s, rank r 
where s.s_num = r.s_num 
and r.rank_start = #{rank_start}
and r.rank_end = #{rank_end}
and rownum <= #{pageNum}*#{amount}
and s_genre = #{s_select_genre}
order by rank_rank
) ranklist
where rnum>(#{pageNum}-1)*#{amount}
